import "./App.css";
import { useState } from "react";

function App() {
  const [pages, setPages] = useState(process.env.REACT_APP_PAGES.split(";"));
  const [pos, setPos] = useState(0);
  setTimeout(function () {
    console.log("reinicio");
    setPages(process.env.REACT_APP_PAGES.split(";"));
  }, 9000);
  setTimeout(function () {
    console.log("cambio de pagina");
    if (pos < pages.length - 1) setPos(pos + 1);
    else setPos(0);
  }, 3000);
  return (
    <div className="App">
      {pages.map((url, index) => (
        <iframe
          src={url}
          title="description"
          style={{ display: index == pos ? "block" : "none" }}
        />
      ))}
    </div>
  );
}

export default App;
