import "./App.css";
import { useState } from "react";

function App() {
  const pages = process.env.REACT_APP_PAGES.split(";");
  const [pos, setPos] = useState(0);
  setTimeout(function () {
    document.location.reload(true);
  }, 60 * 60 * 1000);
  setTimeout(function () {
    if (pos < pages.length - 1) setPos(pos + 1);
    else setPos(0);
  }, 5 * 1 * 1000);
  return (
    <div className="App">
      {pages.map((page, index) => {
        const [type, url] = page.split("|");
        if (type === "url")
          return (
            <iframe
              src={url}
              title="description"
              style={{ display: index === pos ? "block" : "none" }}
            />
          );
        if (type === "img") return <img src={url} />;
      })}
    </div>
  );
}

export default App;
